import React, {useState} from 'react';
import './App.css';

function App() {

  const[inputList, setInputList] = useState();

  const newItem = () =>{
    setInputList();
  };


  return (
    <>
    <div className='main_div'>
      <div className='center_div'>
      <br/>
      <h1>ToDo List</h1>
      <br />
      <input type ="text" placeholder='Add new Item' onChange={newItem} />
      <button> + </button>


      <ol>
        <li>{inputList}</li>
      </ol>
      </div>

    </div>
    </>
  );
}

export default App;
